  OFCHECK(invalid_id != (id = OFiconv_open("EUC-KR", "UTF-8")));
  if (id != invalid_id) OFiconv_close(id);
  OFCHECK(invalid_id != (id = OFiconv_open("GBK", "UTF-8")));
